package com.cloudmachine.net;import java.io.BufferedReader;import java.io.IOException;import java.io.InputStream;import java.io.InputStreamReader;import java.io.UnsupportedEncodingException;/** * 2013-10-17 * * @author coca */public class IOUtil {      /**       * @param is InputStream       * @return String   IOUtil.inputStreamToString     * @throws UnsupportedEncodingException        */      public static String inputStreamToString(InputStream is) throws UnsupportedEncodingException {            BufferedReader reader = new BufferedReader(new InputStreamReader(is,"UTF-8"));            StringBuilder sb = new StringBuilder();            String line = null;            try {                  while ((line = reader.readLine()) != null) {                        sb.append(line);                  }            } catch (IOException e) {            } finally {                  try {                        is.close();                  } catch (IOException e) {                        e.printStackTrace();                  }            }            return sb.toString();      }}