package com.cloudmachine.utils;import android.content.Context;import android.content.SharedPreferences;public class VerisonCheckSP {		public static void insertTime(Context context, long time) {		SharedPreferences sp = context.getSharedPreferences("versiontime",				Context.MODE_PRIVATE);		SharedPreferences.Editor edit = sp.edit();		edit.putLong("time", time);		edit.commit();	}	public static long getTime(Context context) {		SharedPreferences sp = context.getSharedPreferences("versiontime",				Context.MODE_PRIVATE);		return sp.getLong("time", 0);	}}